# -*- coding: utf-8 -*-"""Created on Fri Oct 30 14:46:44 2020@author: Ayman Al Jabri"""import dlibimport cv2kwargs = {        'scaleFactor':1.1,        'minNeighbors':4,        'minSize':(50,50),        'maxSize':None        }fname = 'haarcascade_frontalface_default.xml'class HOG():    def __init__(self):        self.clf = dlib.get_frontal_face_detector()    def find_faces(self,img):        gray = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)        return self.clf(gray)    def draw_rect(self, frame, faces, text=None):        if faces  is None: return frame        for box in faces:            x,y,h,w = box.left(),box.top(),box.right(),box.bottom()            cv2.rectangle(frame, (x, y), (h, w), (80,18,236), 2)            cv2.rectangle(frame, (x, y), (h, y-15), (80,18,236), cv2.FILLED)            cv2.putText(frame, 'Unknown', (x + 6, y - 2), cv2.FONT_HERSHEY_DUPLEX, 0.5, (255, 255, 255), 1)        return frame